import React from "react"
import { Link, withRouter } from "react-router-dom"
import styled from "styled-components"

const Header = styled.header`
  color: wthie;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 50px;
  display: flex;
  align-items: center;
  padding: 0px 10px;
  background-color: rgba(20, 20, 20, 0.8);
  box-shadow: 0px 1px 5px 2px rgba(0, 0, 0, 0.8);
`;

const List = styled.div`
  display: flex;
`

const MenuList = styled.ul`
  display: flex;
  justify-content: left;
`;

const MenuItem = styled.li`
  width: 80px;
  text-align: center;
  border-bottom: 4px solid
    ${(props) => (props.current ? "#3498db" : "transparent")}; // flatuicolors.com
  transition: border-bottom 0.5s ease-in-out;
`;

const UserList = styled.ul`
  display: flex;
  justify-content: right;
`

const UserItem = styled.li`

`

const SLink = styled(Link)`
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
`;

export default withRouter(({ location: { pathname } }) => (
  <Header className="nav">
    <List>
      <MenuList>
        <MenuItem current={pathname === "/"}>
          <SLink to="/">Home</SLink>
        </MenuItem>
        <MenuItem current={pathname === "/mates"}>
          <SLink to="/mates">Mates</SLink>
        </MenuItem>
        <MenuItem current={pathname === "/projects"}>
          <SLink to="/projects">Projects</SLink>
        </MenuItem>
      </MenuList>
      {
        // 세션 검사  (로그인 / 로그아웃)
      }
      <UserList>
        <UserItem>
          <SLink to="/sign">로그인</SLink>
        </UserItem>
      </UserList>
    </List>
  </Header>
));
